import React, {useState, useEffect, useContext} from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header'
import Footer from '../components/Footer'
import axios from 'axios';
import { AuthContext } from "../Auth";
import { useParams } from "react-router-dom";

function Diary() {
  const {date} = useParams();
  const initFormContent = {
    diary: {
      jaContent: "",
      date: date
    },
    diaryContent: {
      languageId: 1,
      content: ""
    }
  }

  const [FormContent, setFormContent] = useState(initFormContent);
  const [languages, setLanguages] = useState([]);
  const [isEdit, setIsEdit] = useState(false);

  const componentDidMount = () => {
    axios.get(`/languages`)
      .then(res => {
        const languages = res.data;
        setLanguages(languages);
        // console.log(languages)
      })
    axios.get(`/diaries/${FormContent.diary.date}`)
      .then(res => {
        const ExistDiary = res.data;
        console.log(ExistDiary)
        if(ExistDiary.date){
          setIsEdit(true);
          setExistDiary(ExistDiary);
        }
      })
  }

  useEffect(componentDidMount, [])

  const setExistDiary = (ExistDiary) => {
    setFormContent(
      {
        diary: {
          ...FormContent.diary,
          jaContent: ExistDiary.ja_content
        },
        diaryContent: {
          ...FormContent.diaryContent,
          languageId: ExistDiary.diary_contents[0].language_id,
          content: ExistDiary.diary_contents[0].content
        }

      }
    );
  };

  const handleChangeLanguage = (e) => {
    setFormContent(
      {
        diary: {
          ...FormContent.diary,
        },
        diaryContent: {
          ...FormContent.diaryContent,
          languageId: e.target.value
        }
      }
    );
  };

  const handleChangeJapaneseDiary = (e) => {
    setFormContent(
      {
        diary: {
          ...FormContent.diary,
          jaContent: e.target.value
        },
        diaryContent: {
          ...FormContent.diaryContent
        }
      }
    );
  };

  const handleChangeDiaryContent = (e) => {
    setFormContent(
      {
        diary: {
          ...FormContent.diary
        },
        diaryContent: {
          ...FormContent.diaryContent,
          content: e.target.value
        }
      }
    )
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if(isEdit){
      axios.patch(`/diaries/${FormContent.diary.date}`, FormContent)
        .then(res =>{
          console.log(res)
        })
    }else{
      axios.post(`/diaries`, FormContent)
        .then(res => {
          console.log(res)
        })
    }
  }

  return(
    <>
      <form onSubmit={handleSubmit}>
        <label>日本語日記</label>
        <textarea onChange={handleChangeJapaneseDiary}></textarea>
        <br/>
        <label>外国語日記</label>
        <textarea onChange={handleChangeDiaryContent}></textarea>
        <br/>
        <label>言語選択：</label>
        <select onChange={handleChangeLanguage}>
          {languages.map(language => 
            <option key={language.id} value={language.id}>
              {language.name}
            </option>)
          }
        </select>
        <br/>
        <button type="submit">でけた！</button>
      </form>
    </>
  )
}

export default Diary;
