import React, {useState, useEffect, useContext} from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header'
import Footer from '../components/Footer'
import axios from 'axios';
import { AuthContext } from "../Auth";

function UserEdit() {
  const initSession =
    {
      email: '',
      password: ''
    }
  const initUser =
    {
      email: '',
      password: '',
      passwordConfirmation: '',
      languageId: ''
    }
  const [user, setUser] = useState(initUser);
  const [session, setSession] = useState(initSession);
  const [languages, setLanguages] = useState([]);

  const componentDidMount = () => {
    axios.get(`/languages`)
      .then(res => {
        const languages = res.data;
        setLanguages(languages);
        // console.log(languages)
      })
  }

  useEffect(componentDidMount, [])

  const handleChangeSessionEmail = (e) => {
    setSession(
      {
        ...session,
        email: e.target.value
      }
    );
  };

  const handleChangeSessionPassword = (e) => {
    setSession(
      {
        ...session,
        password: e.target.value
      }
    );
  };

  const handleChangeEmail = (e) => {
    setUser(
      {
				...user,
				email: e.target.value
			}
    );
  };

  const handleChangePassword = (e) => {
    setUser(
			{
				...user,
				password: e.target.value
			}
    );
  };

  const handleChangePasswordConfirmation = (e) => {
    setUser(
			{
				...user,
				passwordConfirmation: e.target.value
			}
    );
  };

  const handleChangeLanguage = (e) => {
    setUser(
      {
        ...user,
        languageId: e.target.value
      }
    );
  };


  const handleClickUpdate = (columnNames) => {
    const reducer = (beforeResult, columnName) => {
      beforeResult[`${columnName}`] = user[columnName]
      console.log(beforeResult)
      return beforeResult
    };
    console.log(columnNames)
    const userParams = columnNames.reduce(reducer, {})
    axios.patch(`/users`, {
      session: session, 
      user: userParams
    })
  };

  return(
    <>
      <form>
        <h2>本人確認</h2>
        <label>メールアドレス：</label>
        <input type="text"
          name="email"
          onChange={handleChangeSessionEmail}
        />

        <br/>

        <label>パスワード：</label>
        <input type="text"
          name="password"
          type="password"
          onChange={handleChangeSessionPassword}
        />

        <label>変更後メールアドレス：</label>
        <input type="text"
          name="email"
          onChange={handleChangeEmail}
        />
        <button onClick={handleClickUpdate(["email"])}>変更する</button>

        <br/>

        <label>変更後パスワード：</label>
        <input type="text"
          name="password"
          type="password"
          onChange={handleChangePassword}
        />

        <br/>

        <label>パスワード確認用：</label>
        <input type="text"
          name="password_confirmation"
          type="password"
          onChange={handleChangePasswordConfirmation}
        />
        <button onClick={handleClickUpdate(["password", "passwordConfirmation"])}>変更する</button>


        <br/>

        <label>言語選択：</label>
        <select onChange={handleChangeLanguage}>
          {languages.map(language => 
            <option key={language.id} value={language.id}>
              {language.name}
            </option>)
          }
        </select>
        <button onClick={handleClickUpdate(["languageId"])}>変更する</button>

      </form>
    </>
  )
}
export default UserEdit;
