import { Link } from 'react-router-dom'
import Header from '../components/Header'
import Footer from '../components/Footer'

import React, { useState } from 'react';
import { createStyles, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import moment from 'moment';
import { DayPickerRangeController } from 'react-dates';
import IconButton from '@material-ui/core/IconButton';
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore';
import NavigateNextIcon from '@material-ui/icons/NavigateNext';

import { format, compareAsc } from 'date-fns'
import 'moment/locale/ja';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import { SingleDatePicker } from 'react-dates';

import '../styles/css/react-dates-custom.css';

function CustomReactDatesDateRangePicker() {
  const classes = useStyles();
  const dateFormat = 'YYYY/MM/DD';
  const [date, setDate] = useState();
  const [focused, setFocused] = useState(false);
  return (
    <>
      {/* <SingleDatePicker
        date={date}
        onDateChange={date => setDate(date)}
        focused={focused}
        onFocusChange={focused => setFocused(focused)}
        id="date"
        onClose={focused => setFocused(false)}
      /> */}
      <TextField
        className={classes.textField}
        label="react-dates"
        value={data}
        onFocus={() => setFocused(true)}
      />
      <DayPickerRangeController
        navPrev={ // 前の月に戻るボタン ★
          <IconButton>
            <NavigateBeforeIcon />
          </IconButton>
        }
        navNext={ // 次の月に進むボタン ★
          <IconButton>
            <NavigateNextIcon />
          </IconButton>
        }
        hideKeyboardShortcutsPanel={true} // 右下の?ボタンをなくす
        date={date}
        onDateChange={date => setDate(date)}
        focused={focused}
        onFocusChange={focused => setFocused(focused)}
        id="date"
        onClose={focused => setFocused(false)}
      />
    </>
  );
}

export default CustomReactDatesDateRangePicker;

