import React, {useState, useEffect, useContext} from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header'
import Footer from '../components/Footer'
import axios from 'axios';
import { AuthContext } from "../Auth";

function UserEdit() {
  const initUser =
    {
      email: '',
      password: '',
      passwordConfirmation: '',
      languageId: ''
    }
  const [user, setUser] = useState(initUser);
  const [languages, setLanguages] = useState([]);

  const componentDidMount = () => {
    axios.get(`/languages`)
      .then(res => {
        const languages = res.data;
        setLanguages(languages);
        // console.log(languages)
      })
  }

  useEffect(componentDidMount, [])

  const handleChangeEmail = (e) => {
    setUser(
      {
				...user,
				email: e.target.value
			}
    );
  };

  const handleChangePassword = (e) => {
    setUser(
			{
				...user,
				password: e.target.value
			}
    );
  };

  const handleChangePasswordConfirmation = (e) => {
    setUser(
			{
				...user,
				passwordConfirmation: e.target.value
			}
    );
  };

  const handleChangeLanguage = (e) => {
    setUser(
      {
        ...user,
        languageId: e.target.value
      }
    );
  };


  handleClickUpdate = (columnNames) => {
    const userParams = columnNames.reduce((beforeResult, columnName) => {
      beforeResult[`${columnName}`] = user[columnName]
      return beforeResult
    }, {})
    axios.patch(`/users`, {
      session: session, 
      user: userParams
    })
  }

  return(
    <>
      <form onSubmit={handleSubmit}>
        <label>メールアドレス：</label>
        <input type="text"
          name="email"
          onChange={handleChangeEmail}
        />
        <button onClick={handleClickUpdate("email")}>変更する</button>

        <br/>

        <label>パスワード：</label>
        <input type="text"
          name="password"
          type="password"
          onChange={handleChangePassword}
        />

        <br/>

        <label>パスワード確認用：</label>
        <input type="text"
          name="password_confirmation"
          type="password"
          onChange={handleChangePasswordConfirmation}
        />
        <button onClick={handleClickUpdate("password")}>変更する</button>


        <br/>

        <label>言語選択：</label>
        <select onChange={handleChangeLanguage}>
          {languages.map(language => 
            <option key={language.id} value={language.id} selected={auth.currentUser.language_id === language.id ? true : false}>
              {language.name}
            </option>)
          }
        </select>
        <button onClick={handleClickUpdate("languageId")}>変更する</button>

      </form>
    </>
  )
}
export default UserEdit;
